package com.class13;

public class StringManipulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//SUBSTRING METHOD
		String str="Java is getting interesting";
		
		System.out.println(str.substring(15));  //gives word after index 15 or everything after each index
		System.out.println(str.substring(8, 15));    //8getting15
		
		
		
		
		
	}

}
